import React from 'react';
import './portfolio.css';

function openOverlay(imageSrc) {
  const overlay = document.getElementById('overlay');
  const overlayImage = document.getElementById('overlay-image');

  overlayImage.src = imageSrc;
  overlay.style.display = 'flex';
}

function closeOverlay() {
  const overlay = document.getElementById('overlay');
  overlay.style.display = 'none';
}

function openFigma(figmaLink) {
  window.open(figmaLink, '_blank');
}

document.addEventListener('DOMContentLoaded', function () {
  const slides = document.querySelectorAll('.slide_active');

  slides.forEach(slide => {
    slide.addEventListener('click', function () {
      slides.forEach(s => s.classList.remove('active'));
      this.classList.add('active');
    });
  });
});

function Portfolio() {
  return (
    <div className='block'>
      <div className="project">
        <div className="carousel">
          <div className="slide_active" onClick={() => openOverlay('1.png')}>
            <img src="1.png" alt="Фотография 1" />
          </div>
          <div className="slide_active" onClick={() => openOverlay('2.png')}>
            <img src="2.png" alt="Фотография 2" />
          </div>
          <div className="slide_active" onClick={() => openOverlay('3.png')}>
            <img src="3.png" alt="Фотография 3" />
          </div>
          {/* Add more slides as needed */}
        </div>
        <div className="project-description">
          <h3>Прототип сайта с тремя страницами, содержание формы регистрации и обратной связи</h3>
          <p>Основные инструменты работы c UI/UX аналитикой</p>
          <button className="view-project-button" onClick={() => openFigma('https://www.figma.com/file/pOTRoLEe6xi3eivQGumZwm/Untitled?type=design&node-id=0%3A1&mode=design&t=cIrW35bSKqHEXRsU-1')}>Посмотреть проект</button>
        </div>
      </div>
      
      <div class="overlay" id="overlay" onClick={closeOverlay}>
        <img src="" alt="Увеличенное изображение" id="overlay-image" />
      </div>
    </div>
  );
}

export default Portfolio;
