import React, { useState } from 'react';

import './header.css'

import Avatar from '@mui/material/Avatar';
import Modal from '@mui/material/Modal';


function Header() {

  const [isModalOpen, setIsModalOpen] = useState(false);




  const handleAvatarClick = () => {
    setIsModalOpen(true);


  };





  const handleCloseModal = () => {

    setIsModalOpen(false);



  }


  return (
    <>
      <header>
        <h1>Портфолио</h1>


      </header>


      <nav>


        <a href="/" class="glow-on-hover">


          <button type="button">ГЛАВНАЯ</button>


        </a>
        <a href="/projects" class="glow-on-hover">


          <button type="button">ПРОЕКТЫ</button>


        </a>
        <a href="/gallery" class="glow-on-hover">


          <button type="button">ГАЛЕРЕЯ</button>


        </a>
        <a href="/contacts" class="glow-on-hover">


          <button type="button">КОНТАКТЫ</button>


        </a>
        <div className='.avatar-container'>
          {/* <a href="/politics">Политика-Х</a> */}
          <Avatar
            alt="Remy Sharp"
            src="/static/images/avatar/2.jpg"
            sx={{ width: 50, height: 50, marginLeft: 'auto' }}
            onClick={handleAvatarClick}
            className="avatar" // Add the 'avatar' class
          />

          <Modal open={isModalOpen} onClose={handleCloseModal} sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', }}>

            {/* Content for the enlarged avatar, you can use the same Avatar component */}

            <Avatar
              alt="Remy Sharp"
              src="/static/images/avatar/2.jpg"
              sx={{ width: '600px', height: '600px' }}
              onClick={handleCloseModal}

            />
            <h1>Гуренко Кристина Викторовна</h1>
            <p>студент: Московского финансово-промышленный университета «Синергия»</p>
            <p>Факультет дополнительного образования </p>
            <p>Веб-дизайн и разработка сайтов</p>
            <p>ИВДРС-1013</p>
            </Modal>
        </div>
      
     


    </nav >



    </>
  );
}


export default Header;
