import React, { useState } from 'react';

import './header.css'

import Avatar from '@mui/material/Avatar';
import Modal from '@mui/material/Modal';


function Header() {
 
    const [isModalOpen, setIsModalOpen] = useState(false);


  

  const handleAvatarClick = () => {
    setIsModalOpen(true);


  };





  const handleCloseModal = () => {

    setIsModalOpen(false);



  }


  return (
    <>
      <header>
        <h1>Портфолио</h1>
        <p>Гуренко Кристина</p>
        <p>студент </p>


      </header>


      <nav>
        <a href="/">Главная</a>
        <a href="/projects">Проекты</a>
        <a href="/gallery">Галерея</a>
        <a href="/contacts">Контакты</a>
        {/* <a href="/politics">Политика-Х</a> */}
        <Avatar
          alt="Remy Sharp"
          src="/static/images/avatar/2.jpg"
          sx={{ width: 60, height: 60, marginLeft: 'auto' }}
          onClick={handleAvatarClick}
        />

<Modal open={isModalOpen} onClose={handleCloseModal} sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>

          {/* Content for the enlarged avatar, you can use the same Avatar component */}
          <Avatar
            alt="Remy Sharp"
            src="/static/images/avatar/2.jpg"
            sx={{ width: 200, height: 200 }} 
            onClick={handleCloseModal}

          />

        </Modal>
      </nav>



    </>
  );
}

export default Header;
