import React from 'react';
import './portfolio.css'




function openOverlay(imageSrc) {
  const [overlayVisible, setOverlayVisible] = useState(false);
  
  const [overlayImageSrc, setOverlayImageSrc] = useState('');

  overlayImage.src = imageSrc;
  overlay.style.display = 'flex';
}

function closeOverlay() {
  const overlay = document.getElementById('overlay');
  overlay.style.display = 'none';
}

function openFigma(figmaLink) {
  window.open(figmaLink, '_blank');
}
document.addEventListener('DOMContentLoaded', function () {
  const slides = document.querySelectorAll('.slide_active');

  slides.forEach(slide => {
    slide.addEventListener('click', function () {

      slides.forEach(s => s.classList.remove('active'));

      this.classList.add('active');
    });
  });
});
function Portfolio() {
  return (
    <div className='block1'>
      <div className="project">
        <div className="carousel">
          <div className="slide_active" onClick={() => openOverlay('1.png')}>
            <img src="1.png" alt="Фотография 1" />
          </div>
          <div className="slide_active" onClick={() => openOverlay('2.png')}>
            <img src="2.png" alt="Фотография 2" />
          </div>
          <div className="slide_active" onClick={() => openOverlay('3.png')}>
            <img src="3.png" alt="Фотография 3" />
          </div>
          {/* Add more slides as needed */}
        </div>
        <div className="project-description">
          <h3>Прототип сайта с тремя страницами, содержание формы регистрации и обратной связи</h3>
          <p>Основные инструменты работы c UI/UX аналитикой</p>
          <a href="#" className="view-project-button" onClick={() => openFigma("openFigma('https://www.figma.com/file/pOTRoLEe6xi3eivQGumZwm/Untitled?type=design&node-id=0%3A1&mode=design&t=cIrW35bSKqHEXRsU-1')")}>Посмотреть проект</a>
        </div>
      </div>
     
      <div className='block1'>
        <div className="project">
          <div className="carousel">
            <div className="slide_active" onClick={() => openOverlay('4.png')}>
              <img src="4.png" alt="Фотография 4" />
            </div>
            <div className="slide_active" onClick={() => openOverlay('5.png')}>
              <img src="5.png" alt="Фотография 5" />
            </div>
            <div className="slide_active" onClick={() => openOverlay('6.png')}>
              <img src="6.png" alt="Фотография 6" />
            </div>
            {/* Add more slides as needed */}
          </div>
          <div class="project-description">
            <h3>Создание проекта используя дизайн Figma в качестве макета.</h3>
            <p>Верстка сайтов (HTML5+CSS3)</p>
            <a href="#" class="view-project-button" onclick="openFigma('https://www.figma.com/file/29pRfrzildwmgqr3Xm7mFs/Simple-Starter-Template-v0.1-(Community)?type=design&mode=design')">Посмотреть проект</a>
          </div>
        </div>
        <div className='block1'>
          <div className="project">
            <div className="carousel">
              <div className="slide_active" onClick={() => openOverlay('7.png')}>
                <img src="7.png" alt="Фотография 7" />
              </div>
              <div className="slide_active" onClick={() => openOverlay('10.png')}>
                <img src="10.png" alt="Фотография 10" />
              </div>
              <div className="slide_active" onClick={() => openOverlay('11.png')}>
                <img src="11.png" alt="Фотография 11" />
              </div>
        
              {/* Add more slides as needed */}
            </div>
            <div className="project-description">
              <h3>Многостраничный сайт в Wordpress</h3>
              <p>Разработка сайтов на основе CMS</p>
              <a href="#" className="view-project-button" onClick={() => openFigma("openGithub('https://github.com/gurenko30/calculator.git')")}>Посмотреть проект</a>
            </div>
          </div>
        </div>
        <div className='block1'>
          <div className="project">
            <div className="carousel">
              <div className="slide_active" onClick={() => openOverlay('15.png')}>
                <img src="15.png" alt="Фотография 15" />
              </div>
              <div className="slide_active" onClick={() => openOverlay('16.png')}>
                <img src="16.png" alt="Фотография 16" />
              </div>
