import React, { useState } from 'react';

import './header.css'

import Avatar from '@mui/material/Avatar';


function Header() {
  const [isAvatarHovered, setIsAvatarHovered] = useState(false);

  return (
    <>
      <header>
        <h1>Портфолио</h1>


      </header>


      <nav>


        <a href="/" class="glow-on-hover">


          <button type="button">ГЛАВНАЯ</button>


        </a>
        <a href="/projects" class="glow-on-hover">


          <button type="button">ПРОЕКТЫ</button>


        </a>
        <a href="/gallery" class="glow-on-hover">


          <button type="button">ГАЛЕРЕЯ</button>


        </a>
        <a href="/contacts" class="glow-on-hover">


          <button type="button">КОНТАКТЫ</button>
        </a>


        <div
          className={`avatar-container ${isAvatarHovered ? 'enlarged' : ''}`}
          onMouseEnter={() => setIsAvatarHovered(true)}
          onMouseLeave={() => setIsAvatarHovered(false)}
        >
          <Avatar alt="Remy Sharp" src="/static/images/avatar/2.jpg" />
          {isAvatarHovered && (
            <div className="avatar-info">
              <h1>Гуренко Кристина Викторовна</h1>
              <p>студент: Московского финансово-промышленный университета «Синергия»</p>
              {/* Add more information as needed */}
            </div>
          )}
        </div>


      </nav >



    </>
  );
};


export default Header;
