import './gallery.css'
import * as React from 'react';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';

function Gallery() {
  return (


    <ImageList sx={{ width: 1900, height: 1200 }}>
      <div className='container'>
        <TransitionGroup>
          {groupedItemData.map((group, groupIndex) => (
            <CSSTransition key={groupIndex} timeout={500} classNames="fade">
              <div className="image-group">
                {group.map((item) => (
                  <ImageListItem key={item.img}>
                    <img
                      srcSet={`${item.img}?w=248&fit=crop&auto=format&dpr=2 2x`}
                      src={`${item.img}?w=248&fit=crop&auto=format`}
                      alt={item.title}
                      loading="lazy"
                    />
                    <ImageListItemBar
                      title={item.title}
                      subtitle={<span>by: {item.author}</span>}
                      position="below"
                    />
                  </ImageListItem>
                ))}
              </div>
            </CSSTransition>
          ))}
        </TransitionGroup>
      </div>
    </ImageList>
  );
}

const CSSTransition = [

  {
    img: 'image1.jpg',
    title: 'Image 1',
    author: 'Author 1',
  },

  {

    img: 'image2.jpg',
    title: 'Image 2',
    author: 'Author 2',
  },

  {


    img: 'image3.jpg',


    title: 'Image 3',

    author: 'Author 3',
  },
];



export default Gallery;
