// ProjectsGallery.js

import React from 'react';
import './projects_gallery.css';

const projects = [
  {
    title: 'Проект 1',
    description: 'Краткое описание проекта 1.',
    imageUrl: '/1.jpg', // Путь к изображению проекта 1
    link: 'https://ссылка-на-проект-1.com',
  },
  {
    title: 'Проект 2',
    description: 'Краткое описание проекта 2.',
    imageUrl: '/2.jpg', // Путь к изображению проекта 2
    link: 'https://ссылка-на-проект-2.com',
  },
  // Добавьте больше проектов по аналогии
];

const ProjectsGallery = () => {
  return (
    <div className="projects-gallery">
      <h2>Галерея проектов</h2>
      <div className="projects-container">
        {projects.map((project, index) => (
          <div className="project-item" key={index}>
            <img src={project.imageUrl} alt={project.title} />
            <div className="project-info">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <a href={project.link} target="_blank" rel="noopener noreferrer">
                Посмотреть проект
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectsGallery;
